package com.bignerdranch.android.myfirstappimport android.os.Bundleimport android.util.Logimport androidx.appcompat.app.AppCompatActivityconst val TAG = "MainActivity"const val KEY = "poem"/*                     Осень                       I    Октябрь уж наступил — уж роща отряхает    Последние листы с нагих своих ветвей;    Дохнул осенний хлад — дорога промерзает.    Журча еще бежит за мельницу ручей,    Но пруд уже застыл; сосед мой поспешает    В отъезжие поля с охотою своей,    И страждут озими от бешеной забавы,    И будит лай собак уснувшие дубравы.                    ....                                А.С.Пушкин*/class MainActivity : AppCompatActivity() {    var counter: Int = 0    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_main)        Log.d(TAG, "Октябрь уж наступил — уж роща отряхает")    }    override fun onStart() {        super.onStart()        when {            counter==0  -> Log.d(TAG, "Последние листы с нагих своих ветвей;")            else        -> Log.d(TAG, "В отъезжие поля с охотою своей,")        }    }    override fun onResume() {        super.onResume()        when {            counter==0  -> Log.d(TAG, "Дохнул осенний хлад — дорога промерзает.")            else        -> Log.d(TAG, "И страждут озими от бешеной забавы,")        }    }    override fun onPause() {        super.onPause()        if (counter==0)            Log.d(TAG, "Журча еще бежит за мельницу ручей,")    }    override fun onStop() {        super.onStop()        when{            counter==0  ->  {                Log.d(TAG, "Но пруд уже застыл; сосед мой поспешает")                counter++            }            else        -> Log.d(TAG, "И будит лай собак уснувшие дубравы.")        }    }}